import { notFound } from 'hsfvieira-http/src/default.js'
import { getBody, getParams } from 'hsfvieira-http/src/request.js'
import { routes } from 'hsfvieira-http/src/router.js'
import { injectFunctions } from 'hsfvieira-http/src/response.js'

export { routes, get, post } from 'hsfvieira-http/src/router.js'

export function execute(req, response) {
    const {method, url: path} = req
    const res = injectFunctions(response)
    getBody(req, (req) => {
        const filteredRoute = routes[method].filter(route => 
            path.match(route.path)
        )
        if(filteredRoute.length != 0) {
            req.params = getParams(path, filteredRoute[0].path)
            filteredRoute[0].fn(req, res)
        } else {
            notFound(req, res)
        }
    })
}
